package com.programatta.games.demoandroid;

import android.os.Bundle;
import android.util.Log;
import androidx.appcompat.app.AppCompatActivity;
import go.Seq;
import com.programatta.games.demoandroid.corelib.mobile.EbitenView;


public class MainActivity extends AppCompatActivity {
  @Override
  protected void onCreate(Bundle savedInstanceState) {
    super.onCreate(savedInstanceState);
    setContentView(R.layout.activity_main);

    System.out.println("MainActivity: DEBUG: onCreate iniciado.");

    Seq.setContext(getApplicationContext());
    System.out.println("MainActivity: DEBUG: Seq.setContext() llamado.");

    System.out.println("MainActivity: DEBUG: MainActivity onCreate finalizado."); // Nuevo log aqu√≠
  }

  @Override
  protected void onPause() {
    super.onPause();
    Log.v("MainActivity", "Se va a llamar a suspendGame() de EbitenView.....");
    this.getEbitenView().suspendGame();
    Log.v("MainActivity", "Se ha llamado a suspendGame() de EbitenView.....");
  }

  @Override
  protected void onResume() {
    super.onResume();
    Log.v("MainActivity", "Se va a llamar a resumeGame() de EbitenView.....");
    this.getEbitenView().resumeGame();
    Log.v("MainActivity", "Se ha llamado a resumeGame() de EbitenView!");
  }

  private EbitenView getEbitenView() {
      return (EbitenView)this.findViewById(R.id.ebitenview);
  }
}